import React, { Component } from 'react'
import { BrowserRouter, Link, Redirect, Route, Switch } from 'react-router-dom'
import Navbar from './components/layouts/Navbar'
import "bootstrap/dist/css/bootstrap.min.css"
import axios from 'axios'
import Ninjas from './Ninjas';
import Signup from './components/auth/Signup'
import PostShow from './components/posts/show'
import Login from './components/auth/Login'
import CreatePost from './components/posts/create'
import Home from './components/home/user'
import EditPost from './components/posts/edit'



class App extends Component {
    constructor() {
        super();

        this.state = {
            loggedIn: 'loggedOut',
            user: 'noUser'
        }



    }
    componentDidMount() {
        const log = localStorage.getItem('user')
        if (log) {
            this.setState({
                loggedIn: "loggedIn",
                user: log
            })
        }


    }


    render() {

        return (
            <BrowserRouter>
                <div>
                    <Navbar />
                </div>
                <Route exact path='/home'>
                    <Redirect to='/show' />
                </Route>


                <Switch>
                    <Route exact path='/signin'>
                        <Login />
                    </Route>
                    <Route exact path='/edit'>
                        <EditPost />
                    </Route>


                    <Route exact path='/signup'
                        render={props => (
                            <Signup {...props} loggedInStatus={this.state.loggedIn} user={this.state.user} />
                        )}
                    />
                    <Route exact path='/show'
                        render={props => (
                            <PostShow {...props} loggedInStatus={this.state.loggedInStatus} />
                        )}
                    />
                    <Route exact path='/new'>
                        <CreatePost />
                    </Route>


                </Switch>
                <br></br>

                <Route path='/edit/:id'
                    component={EditPost} />



            </BrowserRouter>
        );
    }
}

export default App;
