        this.setState({
            user: "",
            loggedIn: false
        })
        localStorage.clear()
        console.log("Bye bye")
    }
    render(){
    return (
        <ul className="dark">
            <li><NavLink to='/'>Home</NavLink></li>
            <li><NavLink to='/'>Edit Post</NavLink></li>
            <li><NavLink to='/show'>All Posts</NavLink></li>
            <button onClick = {this.handleLogout}> logout</button>

        </ul>
    )
    }
}

export default SignedInLinks
