import React, { Component } from 'react'
import "bootstrap/dist/css/bootstrap.min.css"
import axios from 'axios'

class App extends Component {
    state = {
        fullName: '',
        username: '',
        email: '',
        password: '',
        posts: []
    };
    componentDidMount = () => {
        this.getData()
    }

    getData = () => {
        axios.get('http://localhost:4000/app/api')
            .then((Response) => {
                const data = Response.data
                this.setState({ posts: data });
                console.log(Response.data);
                this.setState({ posts: data });
                console.log("Data has been recieved");

            })
            .catch(() => {
                alert("No data sent");
            })
    }



    handleChange = (event) => {
        const target = event.target;
        const name = target.name;
        const value = target.value;
        this.setState({
            [name]: value
        });
    };
    onSubmit = (event) => {
        event.preventDefault()

        const payload = {
            fullName: this.state.fullName,
            username: this.state.username,
            email: this.state.email,
            password: this.state.password

        };

        axios.post('http://localhost:4000/app/signup', payload)
            .then(Response => console.log(Response.data))


        this.setState({
            fullName: '',
            username: '',
            email: '',
            password: ''
        })
    };

    displayData = (posts) => {
        if (!posts.length) return null;

        return posts.map((posts, index) => {
            <div key={index}>
                <h3>{posts.fullName}</h3>
                <h2>{posts.username}</h2>
                <p>{posts.email}</p>
                <h1>{posts.password}</h1>


            </div>

        })
    }


    render() {
        console.log('State', this.state);
        return (
            <div>
                <div className='container'>
                    <div className='form-div'>
                        <form onSubmit={this.onSubmit}>
                            <input type='text'
                                placeholder='Full Name'
                                name="fullName"
                                onChange={this.handleChange}
                                value={this.state.fullName}
                                className='form-control form-group'
                            />
                            <input type='text'
                                placeholder='Username'
                                name="username"
                                onChange={this.handleChange}
                                value={this.state.username}
                                className='form-control form-group'
                            />
                            <input type='text'
                                placeholder='E-mail'
                                name="email"
                                onChange={this.handleChange}
                                value={this.state.email}
                                className='form-control form-group'
                            />
                            <input type='password'
                                name="password"
                                placeholder='Password'
                                onChange={this.handleChange}
                                value={this.state.password}
                                className='form-control form-group'
                            />
                            <input type='submit' className='btn btn-danger btn-block' value='Submit' />


                        </form>



                    </div>
                </div>
                <div className="blog-">
                    {this.displayData(this.state.posts)}
                </div>
            </div>
        );
    }
}

export default App;
