import React, { Component } from 'react'
import { BrowserRouter, Link, Redirect, Route, Switch } from 'react-router-dom'
import Navbar from './components/layouts/Navbar'
import "bootstrap/dist/css/bootstrap.min.css"
import axios from 'axios'
import Ninjas from './Ninjas';
import Signup from './components/auth/Signup'
import PostShow from './components/posts/show'
import Login from './components/auth/Login'
import CreatePost from './components/posts/create'
import Home from './components/home/user'
import EditPost from './components/posts/edit'
import SignedInLinks from './components/layouts/SignedIn'
import SignedOutLinks from './components/layouts/SignedOut'
import './app.css'
import 'bootstrap/dist/css/bootstrap.min.css';



class App extends Component {
    constructor() {
        super();

        this.state = {
            loggedIn: false,
            user: 'loggedOut'
        }



    }
    componentDidMount() {
        const log = localStorage.getItem('user')
        if (log) {
            this.setState({
                loggedIn: true,
                user: log
            })
        }


    }


    render() {

        return (

            <BrowserRouter>




                <Route exact path='/show'>
                    <Redirect to='/show' />
                </Route>


                <Switch>
                    <Route path='/'>
                        <Navbar logged={this.state.user} />
                    </Route>
                    <Route exact path='/signin'>
                        <Login />
                    </Route>
                    <Route exact path='/edit'>
                        <EditPost />
                    </Route>




                    <Route exact path='/signup'>

                        <Signup logged={this.state.user} />

                    </Route>
                    <Route exact path='/show'
                        render={props => (
                            <PostShow {...props} loggedInStatus={this.state.user} />
                        )}
                    />
                    <Route exact path='/new'>
                        <CreatePost />
                    </Route>


                </Switch>
                <br></br>

                <div>
                    <h1>Welcome</h1>
                    <h1 className='center'>  <Link to='/signin'>Sign In</Link></h1>
                    <br />
                    <Link to='/signup'>Sign Up</Link>
                    <br />
                    <Link to='/show'>View posts</Link>
                    <br />
                    <Link to='/new'>Create New Post</Link>
                    <Link to='/nav'>Navbar</Link>
                </div>



            </BrowserRouter>
        );
    }
}

export default App;
