import React, { Component } from 'react'
import axios from 'axios'
class PostShow extends Component {
    state = {
        fullName: '',
        username: '',
        email: '',
        password: '',
        posts: []
    };
    componentDidMount = () => {
        this.getData()
    }
    getData = () => {
        axios.get('http://localhost:4000/app/show')
            .then((Response) => {
                const data = Response.data
                this.setState({ posts: data });
                console.log(Response.data);
                console.log("Data has been recieved");

            })
            .catch(() => {
                alert("No data sent");
            })
    }
    displayData = (posts) => {
        if (!posts.length) return null;

        return posts.map((posts, index) => (
            <div key={index}>
                <h3>Title: {posts.title}</h3>
                <h2>username:{posts.username}</h2>
                <p>email:{posts.email}</p>
                <h1>Password:{posts.password}</h1>
            </div>

        ));
    };


    render() {
        return (
            <div className="container">
                <div>
                    <h1>These are all my Posts</h1>
                </div>
                <div className="blog-">


                    {this.displayData(this.state.posts)}


                </div>
            </div>
        )
    }
}

export default PostShow
