import React, { Component } from 'react'
import axios from 'axios'
import EditPost from './edit'
import { Redirect, Route } from 'react-router-dom';
import {
    Card, Button, CardImg, CardTitle, CardText, CardGroup,
    CardSubtitle, CardBody
} from 'reactstrap';


class PostShow extends Component {
    constructor(props) {
        super(props);
    }
    state = {
        fullName: '',
        username: '',
        email: '',
        password: '',
        posts: [],
        user: '',
        loggedIn: '',
        edit: ''
    };

    componentDidMount = () => {
        this.getData()
        const log = localStorage.getItem('user');
        if (log) {
            this.setState({
                user: log,
                loggedIn: true
            })
        }
        console.log(this.state.loggedIn)
        console.log(this.state.posts)

    }
    getData = () => {
        const usern = localStorage.getItem('user')
        const payload = {
            username: usern
        }

        axios.post('http://localhost:4000/app/posts', payload)
            .then((Response) => {
                const data = Response.data
                this.setState({ posts: data });


                console.log("Data has been recieved");

            })
            .catch(() => {
                alert("No data sent");
            })
    }
    displayData = (posts) => {

        if (!posts.length) return null;

        return posts.map((posts, index) => (
            <CardGroup>
                <Card>

                    <CardBody>
                        <CardTitle tag="h5">{posts.title}</CardTitle>
                        <CardSubtitle tag="h6" className="mb-2 text-muted">{posts.description}</CardSubtitle>
                        <CardText>{posts.body}</CardText>
                        <Button>Button</Button>
                    </CardBody>
                </Card>
            </CardGroup>

        ));
    };
    Editpost = (data) => {
        // console.log(this.posts.title)
        console.log("helllo");
        let id = data._id
        console.log("id is->" + id);
        localStorage.setItem('id', id)
        this.setState({
            edit: "yes"

        })
        //console.log(data)
        console.log(this.state.edit)

    }
    DeletePost = (data) => {
        // console.log(this.posts.title)
        console.log("deleting LETSGOO");


        let uId = data._id
        const payload = {
            id: uId

        };
        console.log(payload)


        axios.delete('http://localhost:4000/app/posts/delete', { data: payload })
            .then(Response => console.log("DONE?"))
            .catch((error) => {
                console.log(error)
            })
        this.setState({
            posts: this.state.posts.filter(el => el._id != uId)
        })

        //window.location.href = '/show';




    }
    handleLogout = () => {
        this.setState({
            user: "",
            loggedIn: false
        })
        localStorage.clear()
        console.log("Bye bye")
    }



    render() {
        if (!this.state.loggedIn) {
            return (
                <div>
                    <h1> Login to view all your posts</h1>
                </div>
            )
        }
        if (this.state.edit === "yes") {
            return (
                <Redirect to='/edit'></Redirect>
            )
        }

        return (
            <div className="container">
                <div>
                    <h1>Hi{this.state.user}</h1>
                    <h1>These are all my Posts</h1>
                </div>
                <div className="blog-">


                    {this.displayData(this.state.posts)}


                </div>
                <button onClick={this.handleLogout}> logout</button>


            </div>
        )

    }
}

export default PostShow
