import React, { Component } from 'react'
import axios from 'axios'
import EditPost from './edit'
import { Redirect, Route } from 'react-router-dom';

class PostShow extends Component {
    constructor(props) {
        super(props);
    }
    state = {
        fullName: '',
        username: '',
        email: '',
        password: '',
        posts: [],
        user: '',
        loggedIn: ''
    };

    componentDidMount = () => {
        this.getData()
        const log = localStorage.getItem('user');
        if (log) {
            this.setState({
                user: log,
                loggedIn: true
            })
        }
        console.log(this.state.loggedIn)
        console.log(this.state.posts)

    }
    getData = () => {
        const usern = localStorage.getItem('user')
        const payload = {
            username: usern
        }

        axios.post('http://localhost:4000/app/posts', payload)
            .then((Response) => {
                const data = Response.data
                this.setState({ posts: data });


                console.log("Data has been recieved");

            })
            .catch(() => {
                alert("No data sent");
            })
    }
    displayData = (posts) => {

        if (!posts.length) return null;

        return posts.map((posts, index) => (
            <div key={index}>

                <h3>Title: {posts.title}</h3>
                <h3>username:{posts.username}</h3>
                <h3>description:{posts.description}</h3>
                <h3>body:{posts.body}</h3>
                <button onClick={() => this.Editpost(posts)}>Edit Post</button>
                <button onClick={() => this.DeletePost(posts)}>Delete</button>
            </div>

        ));
    };
    Editpost = (data) => {
        // console.log(this.posts.title)
        console.log("helllo");
        window.location.href = '/edit/' + data._id;
        //console.log(data)


    }
    handleLogout = () => {
        this.setState({
            user: "",
            loggedIn: false
        })
        localStorage.clear()
        console.log("Bye bye")
    }



    render() {
        if (!this.state.loggedIn) {
            return (
                <div>
                    <h1> Login to view all your posts</h1>
                </div>
            )
        }

        return (
            <div className="container">
                <div>
                    <h1>Hi{this.state.user}</h1>
                    <h1>These are all my Posts</h1>
                </div>
                <div className="blog-">


                    {this.displayData(this.state.posts)}


                </div>
                <button onClick={this.handleLogout}> logout</button>


            </div>
        )

    }
}

export default PostShow
