import React, { Component } from 'react'
import axios from 'axios'
class PostShow extends Component {
    constructor(props){
        super(props);
    }
    state = {
        fullName: '',
        username: '',
        email: '',
        password: '',
        posts: []
    };
    componentDidMount = () => {
        this.getData()
    }
    getData = () => {
        axios.get('http://localhost:4000/app/show')
            .then((Response) => {
                const data = Response.data
                this.setState({ posts: data });
                console.log(Response.data);
                console.log("Data has been recieved");

            })
            .catch(() => {
                alert("No data sent");
            })
    }
    displayData = (posts) => {
        const curUser = localStorage.getItem('user');
        if(curUser){
            const user = JSON.parse(curUser);
            console.log(user)
        }
        if (!posts.length) return null;

        return posts.map((posts, index) => (
            <div key={index}>
                <h3>Title: {posts.title}</h3>
                <h3>username:{posts.username}</h3>
                <h3>description:{posts.description}</h3>
                <h3>body:{posts.body}</h3>
            </div>

        ));
    };


    render() {
        return (
            <div className="container">
                <div>
                    <h1>Status:{this.props.loggedInStatus}</h1>
                    <h1>These are all my Posts</h1>
                </div>
                <div className="blog-">


                    {this.displayData(this.state.posts)}


                </div>
            </div>
        )
    }
}

export default PostShow
