import React, { Component } from 'react'
import axios from 'axios'
import { Redirect } from 'react-router-dom'
import { useState } from 'react'
import PostShow from '../posts/show'
import { Link } from 'react-router-dom'

export default class Login extends Component {


    state = {
        username: '',
        password: '',
        loggedIn: '',
        posts: [],
        user: '',
        submit: false
    };
    componentDidMount = () => {
        this.getData()

        const log = localStorage.getItem('user');

        if (log) {
            this.setState({ loggedIn: true })
            console.log("Still logged in baby")
            console.log(this.state.username)
            console.log(this.state.user)
        }
        else {
            console.log("im out shit")
        }

    }
    handleLogout = () => {
        this.setState({
            user: "",
            loggedIn: false
        })
        localStorage.clear()
        console.log("Bye bye")
    }

    getData = () => {
        const usern = localStorage.getItem('user')
        const payload = {
            username: usern
        }

        axios.post('http://localhost:4000/app/posts', payload)
            .then((Response) => {
                const data = Response.data
                this.setState({ posts: data });
                console.log(this.state.posts)


                console.log("Data has been recieved");

            })
            .catch(() => {
                alert("No data sent");
            })
    }
    displayData = (posts) => {



        return posts.map((posts, index) => (
            <div key={index}>
                <h3>Title: {posts.title}</h3>
                <h3>username:{posts.username}</h3>
                <h3>description:{posts.description}</h3>
                <h3>body:{posts.body}</h3>
            </div>


        ));

    };
    handleChange = (event) => {
        const target = event.target;
        const name = target.name;
        const value = target.value;
        this.setState({
            [name]: value
        });
    };
    submitForm = (event) => {
        event.preventDefault()
        const payload = {
            username: this.state.username,
            password: this.state.password
        };

        const response = axios.post('http://localhost:4000/app/login', payload)
            .then((Response) => {
                this.setState({
                    posts: Response.data

                })
                if (!this.state.posts.length) {
                    this.setState({ loggedIn: false })

                }
                else {
                    this.setState({ loggedIn: true })
                    this.setState({ user: this.state.username })
                    console.log(this.state.username)
                    localStorage.setItem('user', this.state.username)
                    this.setState({
                        username: '',
                        password: '',
                        loggedIn: true

                    })






                }


            })
            .catch(error => console.log(error))

    };


















    render() {
        if (this.state.loggedIn) {
            return <PostShow loggedIn={true} />

        }


        return (
            <div className='container'>
                <form onSubmit={this.submitForm}>
                    <input type='text'
                        placeholder='Username'
                        name="username"
                        onChange={this.handleChange}
                        value={this.state.username}
                        className='form-control form-group'
                    />
                    <input type='password'
                        name="password"
                        placeholder='Password'
                        onChange={this.handleChange}
                        value={this.state.password}
                        className='form-control form-group'
                    />
                    <input type='submit' className='btn btn-danger btn-block' value='Submit' />
                </form>

            </div>
        );
    }
}
