import React, { Component } from 'react'
import axios from 'axios'
import { Redirect } from 'react-router-dom'
import { useState } from 'react'
import {
    Container, Col, Form,
    FormGroup, Label, Input,
    Button,
    FormText,
} from 'reactstrap';
import '../styles/login.css'



export default class Login extends Component {


    state = {
        username: '',
        password: '',
        loggedIn: '',
        posts: [],
        auth: [],
        user: '',
        maybe: ''
    };
    componentDidMount = () => {
        this.getData()

        const log = localStorage.getItem('user');

        if (log) {
            this.setState({ loggedIn: true })
            console.log("Still logged in baby")
            console.log(this.state.username)
            console.log(this.state.user)
        }
        else {
            console.log("im out shit")
        }

    }
    handleLogout = () => {
        this.setState({
            user: "",
            loggedIn: false
        })
        localStorage.clear()
        console.log("Bye bye")
    }

    getData = () => {
        const usern = localStorage.getItem('user')
        const payload = {
            username: usern
        }

        axios.post('http://localhost:4000/app/posts', payload)
            .then((Response) => {
                const data = Response.data
                this.setState({ posts: data });


                console.log("Data has been recieved");

            })
            .catch(() => {
                alert("No data sent");
            })
    }
    displayData = (posts) => {



        return posts.map((posts, index) => (
            <div key={index}>
                <h3>Title: {posts.title}</h3>
                <h3>username:{posts.username}</h3>
                <h3>description:{posts.description}</h3>
                <h3>body:{posts.body}</h3>
            </div>


        ));

    };
    handleChange = (event) => {
        const target = event.target;
        const name = target.name;
        const value = target.value;
        this.setState({
            [name]: value
        });
    };
    submitForm = (event) => {
        event.preventDefault()
        const payload = {
            username: this.state.username,
            password: this.state.password
        };

        const response = axios.post('http://localhost:4000/app/login', payload)
            .then((Response) => {
                if (Response.data === 'Fuck') {
                    this.setState({ maybe: "Incorrect, please try again" })



                }
                else {
                    this.setState({
                        posts: Response.data

                    })
                    this.setState({ loggedIn: true })
                    this.setState({ user: this.state.username })
                    console.log(this.state.username)
                    localStorage.setItem('user', this.state.username)
                    this.setState({
                        username: '',
                        password: '',
                        loggedIn: true

                    })
                }
            })












    };


















    render() {
        if (this.state.loggedIn) {
            return (
                <div className="login">
                    <div className="container">
                        <div>

                            <h1>These are all my Posts</h1>
                            <button onClick={this.handleLogout}> logout</button>

                        </div>
                        <div className="blog-">


                            {this.displayData(this.state.posts)}


                        </div>
                        <button onClick={this.handleLogout}> logout</button>
                    </div>
                </div>
            )

        }

        else {
            <div>Please Login</div>
        }


        return (
            <div className="log">
                <Container className="App col-lg-6 mt-5">

                    <Form className="form" onSubmit={this.submitForm} >
                        <Col>
                            <FormGroup>
                                <Label>User</Label>
                                <Input
                                    type='text'
                                    placeholder='Username'
                                    name="username"
                                    onChange={this.handleChange}
                                    value={this.state.username}
                                />

                            </FormGroup>
                        </Col>
                        <Col>
                            <FormGroup>
                                <Label for="examplePassword">Password</Label>
                                <Input
                                    type="password"
                                    name="password"
                                    id="examplePassword"
                                    placeholder="********"
                                />
                            </FormGroup>
                            <FormText><b>{this.state.maybe}</b></FormText>
                        </Col>
                        <div className='container mt-3'>
                            <Button>Submit</Button>
                        </div>
                    </Form>
                </Container>
            </div>

        );
    }
}
