import React, { Component } from 'react'
import { BrowserRouter, Route } from 'react-router-dom'
import Navbar from './components/layouts/Navbar'
import "bootstrap/dist/css/bootstrap.min.css"
import axios from 'axios'
import Ninjas from './Ninjas';
import PostShow from './components/posts/show'
import SignUp from './components/auth/Signup'



class App extends Component {
    state = {
        fullName: '',
        username: '',
        email: '',
        password: '',
        posts: []
    };
    componentDidMount = () => {
        this.getData()
    }

    getData = () => {
        axios.get('http://localhost:4000/app/api')
            .then((Response) => {
                const data = Response.data
                this.setState({ posts: data });
                console.log(Response.data);
                this.setState({ posts: data });
                console.log("Data has been recieved");

            })
            .catch(() => {
                alert("No data sent");
            })
    }



    handleChange = (event) => {
        const target = event.target;
        const name = target.name;
        const value = target.value;
        this.setState({
            [name]: value
        });
    };
    onSubmit = (event) => {
        event.preventDefault()

        const payload = {
            fullName: this.state.fullName,
            username: this.state.username,
            email: this.state.email,
            password: this.state.password

        };

        axios.post('http://localhost:4000/app/signup', payload)
            .then(Response => console.log(Response.data))


        this.setState({
            fullName: '',
            username: '',
            email: '',
            password: ''
        })
    };

    displayData = (posts) => {
        if (!posts.length) return null;

        return posts.map((posts, index) => (
            <div key={index}>
                <h3>FullName:{posts.fullName}</h3>
                <h2>Username:{posts.username}</h2>
                <p>Email:{posts.email}</p>
                <h1>Password:{posts.password}</h1>
            </div>

        ));
    };



    render() {
        console.log('State', this.state);
        return (
            <BrowserRouter>
                <div>
                    <Navbar />
                </div>

                <Route path='/show' component={PostShow} />
                <Route path='/signup' component={SignUp} />

            </BrowserRouter>
        );
    }
}

export default App;
