import React, { Component } from 'react'
import axios from 'axios'
import { Link, useParams } from 'react-router-dom'
const queryString = require('query-string');
class EditPost extends Component {
    state = {
        username: '',
        title: '',
        description: '',
        body: '',
        loggedIn: '',
        user: '',
        id: ''

    };
    handleChange = (event) => {
        const target = event.target;
        const name = target.name;
        const value = target.value;
        this.setState({
            [name]: value
        });
    };
    componentDidMount = () => {


        const log = localStorage.getItem('user')
        let userId = this.props.match.params.userId;

        console.log(userId)
        if (log) {
            this.setState({
                user: log,
                loggedIn: true,
                id: userId


            })

        }
        this.getData()



    }
    getData = () => {
        let uId = this.props.match.params.userId;
        console.log(uId)
        const payload = {
            id: uId


        }
        axios.post('http://localhost:4000/app/posts/edit', payload)
            .then((Response) => {
                const data = Response.data
                this.setState({
                    title: data.title,
                    body: data.body
                });

                console.log(payload)
                console.log("Data has been recieved");
                console.log(this.state.title)

            })
            .catch(() => {
                alert("No data sent");
            })



    }
    onSubmit = (event) => {
        event.preventDefault()

        const payload = {
            username: this.state.username,
            title: this.state.title,
            description: this.state.description,
            body: this.state.body



        };

        // axios.post('http://localhost:4000/app/edit', payload)
        //   .then(Response => console.log(Response.data))


        this.setState({
            title: '',
            username: '',
            description: '',
            body: ''

        })
    };

    render() {
        //   let url = this.props.location.search
        // let params = queryString.parse(url)
        //console.log(params);
        //let userId = this.props.match.params.userId;
        //console.log(this.userId)

        if (!this.state.loggedIn) {
            return (
                <div>
                    <h1>Please Login</h1>
                    <Link to='/signin'>Sign In</Link>
                    <br />
                    <Link to='/signup'>Sign Up</Link>
                </div>
            )
        }
        return (
            <div>
                <div className='container'>
                    <div className='form-div'>
                        <form onSubmit={this.onSubmit}>
                            <div>Your id is-{this.state.id}</div>
                            <input type='text'
                                placeholder={this.state.title}
                                name="title"
                                onChange={this.handleChange}
                                value={this.state.title}
                                className='form-control form-group'
                            />
                            <input type='text'
                                name="description"
                                placeholder='Desc'
                                onChange={this.handleChange}
                                value={this.state.description}
                                className='form-control form-group'
                            />
                            <input type='description'
                                name="body"
                                placeholder='body'
                                onChange={this.handleChange}
                                value={this.state.body}
                                className='form-control form-group'
                            />


                            <input type='submit' className='btn btn-danger btn-block' value='Submit' />


                        </form>

                        <div>
                            <h1>welcome user, you are {this.state.user}</h1>


                        </div>

                    </div>
                </div>


            </div >

        )
    }
}

export default EditPost
